mapper.select:
SELECT COUNT(*)
FROM CartDTO
WHERE userId = #{userId}
AND pno = #{pno}
AND size = #{size};

mapper.update:
UPDATE CartDTO
SET productCnt = productCnt + #{productCnt}
WHERE userId = #{userId}
AND pno = #{pno}
AND size = #{size};

mapper.delete:
DELETE FROM CartDTO
WHERE userId = #{userId}
AND pno = #{pno}

mapper.update:
UPDATE CartDTO
SET productCnt = #{productCnt}
WHERE pno = #{pno};
