mapper.select:
select
product.pno, pft.storedFileRootName, cno, product_name, product_price, product_stock, product_description, product_regDate, userId
from ProductDTO as product left outer join ProductFileDTO as pft on product.pno = pft.pno where userId = #{userId}
and product_name like concat('%', #{keyword}, '%')
and cno between #{keyword} and #{keyword} + 100
order by product_name limit #{displayTotalContent} , #{pageContent}

select count(*) from ProductDTO where userId = #{userId}
and product_name like concat('%', #{keyword}, '%')
and cno between #{keyword} and #{keyword} + 100

select
product.pno, pft.originalFileName, pft.storedFileRootName, pft.storedThumbNailName, cno, product_name, product_price, product_stock, product_description, product_regDate, userId
from ProductDTO as product left outer join ProductFileDTO as pft on product.pno = pft.pno where product.pno = #{pno}

mapper.update:
update ProductFileDTO
set
originalFileName = #{originalFileName},
storedFileRootName = #{storedFileRootName},
storedThumbNailName = #{storedThumbNailName}
where pno = #{pno}

mapper.select:
select
orderdto.orderNum, orderdto.order_date, detail.pno, detail.productCnt, detail.productPrice, detail.paymentStatus, detail.odNum, orderdto.recipient, orderdto.recipient_phone
FROM OrderDTO orderdto INNER JOIN OrderDetailDTO detail ON orderdto.orderNum = detail.orderNum JOIN ProductDTO product ON detail.pno = product.pno JOIN PaymentDTO payment ON detail.orderDetailNum = payment.orderDetailNum WHERE product.userId = #{userId}
ORDER BY orderdto.order_date LIMIT #{pageContent} ,#{displayTotalContent}

mapper.update:
update OrderDetailDTO
set paymentStatus = #{paymentStatus}
where odNum = #{odNum};
